// eslint-disable-next-line
import React, { useState, useEffect } from "react";
import TinderCard from "react-tinder-card";
import "./Card.css";

// right now facing error in resolving the issue of module not found in firebase.

function Card() {
    // now we have to maintain an array for profile photos of the persons
    // declaring an array in React-Friendly form(By using useState)
    //useState is concept of Hooks api in React.Returns array of 2 entries.

    // eslint-disable-next-line
    const [people, setPeople] = useState([
        {
            name: "Drake",
            url: "data:image/jpeg;base64,"
        },
        {
            name: "Black Widow",
            url: "data:image/jpeg;base64,"
        },
        {
            name: "Dwayne Johnson",
            url: "data:image/jpeg;base64,"
        },
        {
            name: "Taylor Swift",
            url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIC21Q3hLZoj-R6FH97QWPL1IzCuOE5GvBHg&usqp=CAU"
        }
    ]);

    //The last square brackets contain dependencies..Like if we put something in
    //bracket , the code will not run after that part.It will only run once till there

    return (
        <div>
            <div className="cardContainer">
                {
                    // use map for traversing through array for faster rendering to DOM
                    people.map((person) => (
                        <TinderCard
                            className="swipe"
                            key={person.name}
                            preventSwipe={["up", "down"]}
                        >
                            <div
                                style={{
                                    backgroundImage: `url(${person.url})`
                                }}
                                className="card"
                            >
                                <h3>{person.name}</h3>
                            </div>
                        </TinderCard>
                    ))
                }
            </div>
        </div>
    );
}

export default Card;
